types.verbatim:
/* Inclusion of LLD header.*/ #include "hal_spi_lld.h"
#if !defined(SPI_SUPPORTS_CIRCULAR) #error "SPI_SUPPORTS_CIRCULAR not defined in SPI LLD driver" #endif
#if !defined(SPI_SUPPORTS_SLAVE_MODE) #error "SPI_SUPPORTS_SLAVE_MODE not defined in SPI LLD driver" #endif

fields.verbatim:
#if defined(SPI_DRIVER_EXT_FIELS)
SPI_DRIVER_EXT_FIELDS
#endif
/* End of the mandatory fields.*/
spi_lld_driver_fields;

method.implementation:
msg_t msg;

osalDbgCheckClassI();

osalDbgCheck((self != NULL) && (n > 0U));
#if SPI_SUPPORTS_CIRCULAR
osalDbgCheck(((__spi_getfield(self, mode) & SPI_MODE_CIRCULAR) == 0U) ||
             ((n & 1U) == 0U));
#endif

osalDbgAssert(self->state == HAL_DRV_STATE_READY, "not ready");

self->state = HAL_DRV_STATE_ACTIVE;
msg = spi_lld_ignore(self, n);

#if SPI_USE_ASSERT_ON_ERROR == TRUE
osalDbgAssert(msg == HAL_RET_SUCCESS, "function failed");
#endif

return msg;

msg_t msg;

osalSysLock();
msg = spiStartIgnoreI(self, n);
osalSysUnlock();

return msg;

msg_t msg;

osalDbgCheckClassI();

osalDbgCheck((self != NULL) && (n > 0U) &&
             (rxbuf != NULL) && (txbuf != NULL));
#if SPI_SUPPORTS_CIRCULAR
osalDbgCheck(((__spi_getfield(self, mode) & SPI_MODE_CIRCULAR) == 0U) ||
             ((n & 1U) == 0U));
#endif

osalDbgAssert(self->state == HAL_DRV_STATE_READY, "not ready");

self->state = HAL_DRV_STATE_ACTIVE;
msg = spi_lld_exchange(self, n, txbuf, rxbuf);

#if SPI_USE_ASSERT_ON_ERROR == TRUE
osalDbgAssert(msg == HAL_RET_SUCCESS, "function failed");
#endif

return msg;

msg_t msg;

osalSysLock();
msg = spiStartExchangeI(self, n, txbuf, rxbuf);
osalSysUnlock();

return msg;

msg_t msg;

osalDbgCheckClassI();

osalDbgCheck((self != NULL) && (n > 0U) && (txbuf != NULL));
#if SPI_SUPPORTS_CIRCULAR
osalDbgCheck(((__spi_getfield(self, mode) & SPI_MODE_CIRCULAR) == 0U) ||
             ((n & 1U) == 0U));
#endif

osalDbgAssert(self->state == HAL_DRV_STATE_READY, "not ready");

self->state = HAL_DRV_STATE_ACTIVE;
msg = spi_lld_send(self, n, txbuf);

#if SPI_USE_ASSERT_ON_ERROR == TRUE
osalDbgAssert(msg == HAL_RET_SUCCESS, "function failed");
#endif

return msg;

msg_t msg;

osalSysLock();
msg = spiStartSendI(self, n, txbuf);
osalSysUnlock();

return msg;

msg_t msg;

osalDbgCheckClassI();

osalDbgCheck((self != NULL) && (n > 0U) && (rxbuf != NULL));
#if SPI_SUPPORTS_CIRCULAR
osalDbgCheck(((__spi_getfield(self, mode) & SPI_MODE_CIRCULAR) == 0U) ||
             ((n & 1U) == 0U));
#endif

osalDbgAssert(self->state == HAL_DRV_STATE_READY, "not ready");

self->state = HAL_DRV_STATE_ACTIVE;
msg = spi_lld_receive(self, n, rxbuf);

#if SPI_USE_ASSERT_ON_ERROR == TRUE
osalDbgAssert(msg == HAL_RET_SUCCESS, "function failed");
#endif

return msg;

msg_t msg;

osalSysLock();
msg = spiStartReceiveI(self, n, rxbuf);
osalSysUnlock();

return msg;

msg_t msg;

osalDbgCheckClassI();

osalDbgCheck(self != NULL);

osalDbgAssert((self->state == HAL_DRV_STATE_READY) ||
              (self->state == HAL_DRV_STATE_ACTIVE) ||
              (self->state == HAL_DRV_STATE_COMPLETE),
              "invalid state");

if ((self->state == HAL_DRV_STATE_ACTIVE) ||
    (self->state == HAL_DRV_STATE_COMPLETE)) {

  /* Stopping transfer at low level.*/
  msg = spi_lld_stop_transfer(self, np);
  self->state = HAL_DRV_STATE_READY;

#if SPI_USE_SYNCHRONIZATION == TRUE
  osalThreadResumeI(&self->sync_transfer, MSG_RESET);
#endif
}
else {
  msg = HAL_RET_SUCCESS;
}

return msg;

msg_t msg;

osalSysLock();

msg = spiStopTransferI(self, np);
osalOsRescheduleS();

osalSysUnlock();

return msg;

msg_t msg;

osalDbgCheck(self != NULL);
osalDbgAssert((self->state == HAL_DRV_STATE_ACTIVE) ||
              (self->state == HAL_DRV_STATE_READY),
              "invalid state");

if (self->state == HAL_DRV_STATE_ACTIVE) {
  msg = osalThreadSuspendTimeoutS(&self->sync_transfer, timeout);
}
else {
  msg = MSG_OK;
}

return msg;

msg_t msg;

osalSysLock();
msg = spiSynchronizeS(self, timeout);
osalSysUnlock();

return msg;

msg_t msg;

osalSysLock();

msg = spiStartIgnoreI(self, n);
if (msg == MSG_OK) {
  msg = spiSynchronizeS(self, TIME_INFINITE);
}

osalSysUnlock();

return msg;

msg_t msg;

osalSysLock();

msg = spiStartExchangeI(self, n, txbuf, rxbuf);
if (msg == MSG_OK) {
  msg = spiSynchronizeS(self, TIME_INFINITE);
}

osalSysUnlock();

return msg;

msg_t msg;

msg = spiStartSendI(self, n, txbuf);
if (msg == MSG_OK) {
  msg = spiSynchronizeS(self, TIME_INFINITE);
}

osalSysUnlock();

return msg;

msg_t msg;

msg = spiStartReceiveI(self, n, rxbuf);
if (msg == MSG_OK) {
  msg = spiSynchronizeS(self, TIME_INFINITE);
}

osalSysUnlock();

return msg;
